	public UserLoginValidation(String username, String pass, Connection connection) {
		this.userName = username;
		this.pass = pass;
		this.connection = connection;
	}

	public String getUserName() {
		return this.userName;
	}

	public String getPass() {
		return this.pass;
	}
	
	public boolean isMailValid() {
		return ptr.matcher(this.userName).matches();
	}

	public boolean validateUserName() {
		PlayerRepository playerRepository = new PlayerRepository(connection);
		Player player = playerRepository.find("mail", this.userName);
		if (ptr.matcher(this.userName).matches()) {
			
			if (player != null) {
				return ptr.matcher(userName).matches();
			} else {
				return false;
			}
		}
		return false;
	}
	
	public boolean validatePassword(){ 
		PlayerRepository playerRepository = new PlayerRepository(connection);
		Player player = playerRepository.find("mail", this.userName);
		
		if (this.pass.equals(player.getPassword())) {
			return true;
		}
		return false;
	}
	
	public boolean validateCredentials(){ 
		if (!this.validateUserName()) 
			return false;
		if (!this.validatePassword())
			return false;
		return true;
	}

